import { useState } from 'react';
import PromptoPlayer from "../prompto-player/PromptoPlayer";

import '../../assets/documentation.scss';

const labels = [
	{
		'id': 1,
		'title': 'overview',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 2,
		'title': 'dialects',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 3,
		'title': 'keywords',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 4,
		'title': 'comments',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 5,
		'title': 'annotations',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 6,
		'title': 'identifiers',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 7,
		'title': 'types',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 8,
		'title': 'mutability',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 9,
		'title': 'testing',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 10,
		'title': 'methods',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 11,
		'title': 'declaring',
		'parentid': 10,
		'subcategory': true
	}, {
		'id': 12,
		'title': 'calling',
		'parentid': 10,
		'subcategory': true
	}, {
		'id': 13,
		'title': 'arguments',
		'parentid': 10,
		'subcategory': true
	}, {
		'id': 14,
		'title': 'overloading',
		'parentid': 10,
		'subcategory': true
	}, {
		'id': 15,
		'title': 'abstract',
		'parentid': 10,
		'subcategory': true
	}, {
		'id': 16,
		'title': 'statements',
		'parentid': null,
		'subcategory': false
	}, {
		'id': 17,
		'title': 'assignment',
		'parentid': 16,
		'subcategory': true
	}, {
		'id': 18,
		'title': 'if else',
		'parentid': 16,
		'subcategory': true
	}, {
		'id': 19,
		'title': 'switch',
		'parentid': 16,
		'subcategory': true
	}
];

function Categories() {
	const [activeId, setActiveId] = useState(null);

	return (
		labels.map((item, index) => {
			return (
	        	item.subcategory ? (
	        		(item.parentid === activeId) ? (
	        			<li key={ item.id } className="p-dropdown">
							<a href="#">{ item.title }</a>
						</li>
	        		) : (null)
	        	) : (
	        		<li onClick={ () => setActiveId(item.id) } key={ item.id }>
						{/*<a href="#">{ title }</a>*/}
	        			<a href="#" className="uk-flex uk-flex-middle uk-flex-between">
							<span className="uk-margin-small-right">{ item.title }</span>
							{ 
								(item.parentid === activeId) ? (
								<span data-uk-icon="icon: chevron-down"></span> ) : (
								<span data-uk-icon="icon: chevron-right"></span> )
							}
						</a>
			        </li>
	        	)
			)
		})
	)
}

function Reference() {
	return (
        <article className="p-documentation">
            <div className="uk-container uk-container-large uk-padding">
            	<div className="uk-grid uk-grid-large" data-uk-grid>
		            <div className="uk-width-1-5@s">
		                <ul className="uk-tab-right" data-uk-tab="connect: #component-tab-right; animation: uk-animation-fade">
							<Categories />
		                </ul>
		            </div>
		            <div className="uk-width-expand@s">
		                <ul id="component-tab-right" className="uk-switcher">
		                	<li>

		                	</li>
		                </ul>
		            </div>
        		</div>
            </div>
        </article>
    );
}

export default Reference;
