import { useState } from 'react';
import PromptoPlayer from "../prompto-player/PromptoPlayer";

import '../../assets/documentation.scss';

const labels = [
	{
		'id': 1,
		'title': 'overview',
		'parentid': null,
		'active': true
	}, {
		'id': 2,
		'title': 'dialects',
		'parentid': null,
		'active': true
	}, {
		'id': 3,
		'title': 'keywords',
		'parentid': null,
		'active': true
	}, {
		'id': 4,
		'title': 'comments',
		'parentid': null,
		'active': true
	}, {
		'id': 5,
		'title': 'annotations',
		'parentid': null,
		'active': true
	}, {
		'id': 6,
		'title': 'identifiers',
		'parentid': null,
		'active': true
	}, {
		'id': 7,
		'title': 'types',
		'parentid': null,
		'active': true
	}, {
		'id': 8,
		'title': 'mutability',
		'parentid': null,
		'active': true
	}, {
		'id': 9,
		'title': 'testing',
		'parentid': null,
		'active': true
	}, {
		'id': 10,
		'title': 'methods',
		'parentid': null,
		'active': true
	}, {
		'id': 11,
		'title': 'declaring',
		'parentid': 10,
		'active': false
	}, {
		'id': 12,
		'title': 'calling',
		'parentid': 10,
		'active': false
	}, {
		'id': 13,
		'title': 'arguments',
		'parentid': 10,
		'active': false
	}, {
		'id': 14,
		'title': 'overloading',
		'parentid': 10,
		'active': false
	}, {
		'id': 15,
		'title': 'abstract',
		'parentid': 10,
		'active': false
	}, {
		'id': 16,
		'title': 'statements',
		'parentid': null,
		'active': true
	}, {
		'id': 17,
		'title': 'assignment',
		'parentid': 16,
		'active': false
	}, {
		'id': 18,
		'title': 'if else',
		'parentid': 16,
		'active': false
	}, {
		'id': 19,
		'title': 'switch',
		'parentid': 16,
		'active': false
	}
];

function Categories({ data }) {
	const { id, title, parentid, active } = data;

	const initState = {
		class10: false,
		class16: false
	};

	const [activeClass, setActiveClass] = useState(initState);

	const activateClass = (classCategory) => {
		classCategory ? setActiveClass(({[classCategory]: true})) : setActiveClass((initState));
	};

	return (
		<>
			{
	        	!active ? (
	        		<li className={ activeClass['class' + parentid] ? 'p-dropdown active' : 'p-dropdown'}>
						<a href="#">{ title }</a>
					</li>
	        	) : (
	        		<li onClick={ () => activateClass(id, ('class' + id)) }>
						{/*<a href="#">{ title }</a>*/}
	        			<a href="#" className="uk-flex uk-flex-middle uk-flex-between">
							<span className="uk-margin-small-right">{ title }</span>
							{ 
								activeClass['class' + id] ? (
								<span data-uk-icon="icon: chevron-down"></span> ) : (
								<span data-uk-icon="icon: chevron-right"></span> )
							}
						</a>
			        </li>
	        	)
	        }
	    </>
	)
}

function Reference() {


    return (
        <article className="p-documentation">
            <div className="uk-container uk-container-large uk-padding">
            	<div className="uk-grid uk-grid-large" data-uk-grid>
		            <div className="uk-width-1-5@s">
		                <ul className="uk-tab-right" data-uk-tab="connect: #component-tab-right; animation: uk-animation-fade">
		                	{
		                		labels.map((item, index) => <Categories data={ item } key={ 'cat_' + index } />)
		                	}
		                    {/*<li onClick={ () => activateClass() }>
		                    	<a href="#">Overview</a>
		                    </li>
		                    <li onClick={ () => activateClass('class1') }>
								<a href="#" className="uk-flex uk-flex-middle uk-flex-between">
									<span className="uk-margin-small-right">Dialects</span>
									{ 
										activeClass.class1 ? (
										<span data-uk-icon="icon: chevron-down"></span> ) : (
										<span data-uk-icon="icon: chevron-right"></span> )
									}
								</a>
							</li>
							<li className={ activeClass.class1 ? 'p-dropdown active' : 'p-dropdown'}>
								<a href="#">JSON</a>
							</li>*/}
		                </ul>
		            </div>
		            <div className="uk-width-expand@s">
		                <ul id="component-tab-right" className="uk-switcher">
		                	<li>

		                	</li>
		                </ul>
		            </div>
        		</div>
            </div>
        </article>
    );
}

export default Reference;
